}


$bitmapImage = New-Object System.Windows.Media.Imaging.BitmapImage
$bitmapImage.BeginInit()
$bitmapImage.StreamSource = [System.IO.MemoryStream][System.Convert]::FromBase64String($base64Image)
$bitmapImage.EndInit()
